def len=json1.studentRosterDetail.size() ;
def List_1=[1];
def List_2=[1];

for(int i=1;i<len;i++)
{
                def value =json1.studentRosterDetail[i].guid;
               // log.info value
                for(int j=i+1;j<len;j++)
                {

                 def value_1 =json1.studentRosterDetail[j].guid;

                 //if(Integer.parseInt(value_1)==Integer.parseInt(value))
                 if(value.equalsIgnoreCase(value_1))
                 {

                  //List_1.addAll(json1.Data[i].CodigoTipologia).plus(json1.Data[i].PeriodoLetivo).plus(json1.Data[i].DescTipologia).plus(json1.Data[i].TipologiaCodigoDetalhe).plus(json1.Data[i].TipologiaCodigoDetalheDesc).plus(json1.Data[i].CodigoDetalheAplicado).plus(json1.Data[i].CodigoDetalheAplicadoDesc);
              List_1.addAll(json1.studentRosterDetail[i].guid,json1.studentRosterDetail[i].userId,json1.studentRosterDetail[i].firstName);
                              //log.info List_1
              List_2.addAll(json1.studentRosterDetail[j].guid,json1.studentRosterDetail[j].userId,json1.studentRosterDetail[j].firstName);
                            //  log.info List_2
                              try
                              {
                                              assert List_1!=List_2;
                              }
                              catch(Exception e)
                              {
                                              log.info e
                              }
                 }
                }
}

