def failed=0

for (testSuiteRunner in runner.results ) 
{
	for ( testCaseRunner in testSuiteRunner.results )
	{

    	 		if (testCaseRunner.status.toString()=="OK") passed++
    	 			else failed++

	}
}
log.info "passed="+passed+" failed="+failed
runner.testSuite.setPropertyValue("passed", "$passed" ) 
runner.TtestSuite.setPropertyValue("failed", "$failed" )

