con:testSuite.con:tearDownScript:
def passed=0
def failed=0

for (testSuiteRunner in runner.results ) 
{
	for ( testCaseRunner in testSuiteRunner.results )
	{
    		log.info testCaseRunner;
    	 		if (testCaseRunner.status.toString()=="OK") passed++
    	 			else failed++

	}
}
log.info "passed="+passed+" failed="+failed
runner.testSuite.setPropertyValue("passed", "$passed" ) 
runner.testSuite.setPropertyValue("failed", "$failed" ) 
Integer s=passed+failed;
runner.testSuite.setPropertyValue("Totaltestcase", s.toString())

