// verifying the datatypes for all the records 
for(int i=0;i<len;i++)

{
                def value =json1.data[i].codigo;
                if(value==context.getTestCase().getPropertyValue("codigo"))
                {
                assert json1.data[i].codigo instanceof String
                assert json1.data[i].descricao instanceof String
                assert json1.data[i].cursoCodigo instanceof String
                break;

                }

}

import groovy.json.JsonSlurper
boolean length_flag=false;
def response=messageExchange.response.responseContent;
def json1= new JsonSlurper().parseText(response);
def len=json1.data.size() ;
//log.info len
int length;

// verifying the datatypes for all the records 
for(int i=0;i<len;i++)
{
                ///Description field validation

                def value =json1.data[i].codigo;
                def value1 =json1.data[i].descricao;
                def value2 =json1.data[i].cursoCodigo;


                try
                {               
                assert "true"==fieldlengthcheck("codigo_Length",value);
                assert "true"==fieldlengthcheck("descricao_Length",value1);
                assert "true"==fieldlengthcheck("cursoCodigo_Length",value2);
                }
                catch(Exception e)
                {
                              println (json1.data[i].description+": "+e)
                }

}

public int lengthcheck(String description)
{
                   length=description.length();
                   return length;
}

public String fieldlengthcheck(String Max_len_Field_Name,String ResponseFiledname)
{
              length_flag=false;                  //Getting the Max length from the test case

               String fieldlengthfromtestcase=context.getTestCase().getPropertyValue(Max_len_Field_Name);
                int length_testcase=Integer.parseInt(fieldlengthfromtestcase);

                if(lengthcheck(ResponseFiledname)<=length_testcase)


                {

                              length_flag=true;         
                           // log.info  lengthcheck(ResponseFiledname)
                }
                else
                {
                              length_flag=false;

                }
               log.info "before return : "+length_flag
                return length_flag.toString();
}

import groovy.json.JsonSlurper
boolean flar;
def response=messageExchange.response.responseContent;
def json1= new JsonSlurper().parseText(response);
def len=json1.data.size() ;
def List_1=[1];
def List_2=[1];

for(int i=1;i<len;i++)
{
                def value =json1.data[i].codigo;
                //log.info value
                for(int j=i+1;j<len;j++)
                {

                 def value_1 =json1.data[j].codigo;

                 if(value_1.equalsIgnoreCase(value))
                 {

              List_1.addAll(json1.data[i].codigo, json1.data[i]. descricao, json1.data[i]. cursoCodigo);
                            //  log.info List_1
		    List_2.addAll(json1.data[j].codigo, json1.data[j]. descricao, json1.data[j]. cursoCodigo); 
                            //  log.info List_2
                              try
                              {
                                              assert List_1!=List_2;
                              }
