//import com.eviware.soapui.support.GroovyUtils;
//def responseHolder = GroovyUtils.getXmlHolder( testRunner.testCase.testSteps["Test Steps"].testRequest.response.responseContent );

import groovy.json.JsonSlurper
import static java.util.Calendar.*
import com.eviware.soapui.support.GroovyUtils;
import com.eviware.soapui.support.XmlHolder
def DESCRIPTION_resp
def DESCRIPTION
def Holiday_dat_database
def HOL_dat
def HOLIDAY_resp



//Getting the JSON Response from the API
//tstep = testRunner.testCase.testStep.getName();

def ResponseMessage = testRunner.testCase.testSteps["GetHoliday_PositiveTestCase"].testRequest.response.contentAsString
def json1= new JsonSlurper().parseText(ResponseMessage);
def len=json1.data.size() ;

//Getting the OutPut from Data Base using JDBC Request
def responseXML = testRunner.testCase.getTestStepByName("JDBC Request").getResponseContent()
def xml=new XmlHolder(responseXML)
def val=xml.getNodeValue("//ResultSet").size();
def act_val=(val+1)/2
//looping in to the JSON response for gettting all the values 
for(j=0;j<len;j++)
{
	//Response Data input area***********Field name should change for every api*****
	DESCRIPTION_resp=json1.data[j].descricao;
	HOLIDAY_resp=json1.data[j].data;


for( i=1;i<=act_val;i++)
{
//Getting Response from JDBC Request ***********Filed name should change for every api*****

DESCRIPTION=xml.getNodeValue("//ResultSet//Row["+i+"]//DESCRIPTION");
def HOLIDAY=xml.getNodeValue("//ResultSet//Row["+i+"]//HOLIDAY");
//To Match the data alone we used Split
Holiday_dat_database=splitvalu(HOLIDAY,"\\s")
HOL_dat=splitvalu(HOLIDAY_resp,"T");
//log.info "The holiday date: "+HOL_dat;
//log.info "The holiday date from data BASE: "+Holiday_dat_database;
if (DESCRIPTION_resp.equals(DESCRIPTION))
{
	assert DESCRIPTION_resp==DESCRIPTION
	assert HOL_dat==Holiday_dat_database
}
}
}
log.info "Data Match Completed Sucessfully";
///Generic Method *** this is specifically for this API
public String splitvalu(def s,def split_val)
{
	String[] Starr=s.split(split_val);
	def actual=Starr[0];
	return actual
}

