                assert json1.courseDetail[i].guid instanceof String
                assert json1.courseDetail[i].programId instanceof String
                assert json1.courseDetail[i].NRC instanceof String
                assert json1.courseDetail[i].subjectCode instanceof String
                assert json1.courseDetail[i].subjectCourseNumber instanceof String
                assert json1.courseDetail[i].courseName instanceof String
                assert json1.courseDetail[i].sectionStatusCode instanceof String
                assert json1.courseDetail[i].sectionStatus instanceof String
                assert json1.courseDetail[i].programcode instanceof String
                assert json1.courseDetail[i].programName instanceof String
                assert json1.courseDetail[i].gradeVersion instanceof String
                assert json1.courseDetail[i].campusCode instanceof String
                assert json1.courseDetail[i].campus instanceof String
                assert json1.courseDetail[i].courseModalityCode instanceof String
                assert json1.courseDetail[i].courseModality instanceof String
                assert json1.courseDetail[i].cpf instanceof String
                assert json1.courseDetail[i].modalidade instanceof String
                assert json1.courseDetail[i].displayCode instanceof String
               assert json1.courseDetail[i].professores[i].guid instanceof String
                assert json1.courseDetail[i].professores[i].firstName instanceof String
                assert json1.courseDetail[i].professores[i].lastName instanceof String
                assert json1.courseDetail[i].professores[i].mail instanceof String
                assert json1.courseDetail[i].professores[i].isMainProfessor instanceof String
                break;

                }

}

