                def value1 =json1.courseDetail[i].programId;
                def value2 =json1.courseDetail[i].NRC;
                def value3 =json1.courseDetail[i].subjectCode;
                def value4 =json1.courseDetail[i].subjectCourseNumber;
                def value5 =json1.courseDetail[i].courseName;
                def value6 =json1.courseDetail[i].sectionStatusCode;
                def value7 =json1.courseDetail[i].sectionStatus;
                def value8 =json1.courseDetail[i].programcode;
                def value9 =json1.courseDetail[i].programName;
                def value10 =json1.courseDetail[i].gradeVersion;
                def value11 =json1.courseDetail[i].campusCode;
                def value12 =json1.courseDetail[i].campus;
                def value13 =json1.courseDetail[i].courseModalityCode;
                def value14 =json1.courseDetail[i].courseModality;
                def value15 =json1.courseDetail[i].cpf;
                def value16 =json1.courseDetail[i].modalidade;
                def value17 =json1.courseDetail[i].displayCode;
                def value18 =json1.courseDetail[i].guid;
                def value19 =json1.courseDetail[i].firstName;
                def value20 =json1.courseDetail[i].lastName;
                def value21 =json1.courseDetail[i].mail;
                def value22 =json1.courseDetail[i].isMainProfessor;


                try
                {               
                assert "true"==fieldlengthcheck("guid_L",value);
                assert "true"==fieldlengthcheck("programId_L",value1);
                assert "true"==fieldlengthcheck("NRC_L",value2);
                assert "true"==fieldlengthcheck("subjectCode_L",value3);
                assert "true"==fieldlengthcheck("subjectCourseNumber_L",value4);
                assert "true"==fieldlengthcheck("courseName_L",value5);
                assert "true"==fieldlengthcheck("sectionStatusCode_L",value6);
                assert "true"==fieldlengthcheck("sectionStatus_L",value7);
                assert "true"==fieldlengthcheck("programcode_L",value8);
                assert "true"==fieldlengthcheck("programName_L",value9);
                assert "true"==fieldlengthcheck("gradeVersion_L",value10);
                assert "true"==fieldlengthcheck("campusCode_L",value11);
                assert "true"==fieldlengthcheck("campus_L",value12);
                assert "true"==fieldlengthcheck("courseModalityCode_L",value13);
                assert "true"==fieldlengthcheck("courseModality_L",value14);
                assert "true"==fieldlengthcheck("cpf_L",value15);
                assert "true"==fieldlengthcheck("modalidade_L",value16);
                assert "true"==fieldlengthcheck("displayCode_L",value17);
                assert "true"==fieldlengthcheck("guid_L",value18);
                assert "true"==fieldlengthcheck("firstName_L",value19);
                assert "true"==fieldlengthcheck("lastName_L",value20);
                assert "true"==fieldlengthcheck("mail_L",value21);
                assert "true"==fieldlengthcheck("isMainProfessor_L",value22);

                }
                catch(Exception e)
                {
                              println (json1.courseDetail[i].description+": "+e)
                }

