import groovy.json.JsonSlurper
boolean flar;
def response=messageExchange.response.responseContent;
def json1= new JsonSlurper().parseText(response);
def len=json1.data.size() ;
def List_1=[1];
def List_2=[1];

for(int i=1;i<len;i++)
{
                def value =json1.data[i].codigo;
                //log.info value
                for(int j=i+1;j<len;j++)
                {

                 def value_1 =json1.data[j].codigo;

                 if(value_1.equalsIgnoreCase(value))
                 {

              List_1.addAll(json1.data[i]. codigo, json1.data[i]. nome, json1.data[i]. documento, json1.data[i]. matricula, json1.data[i]. email, json1.data[i]. codigoCOA, json1.data[i]. idPlataforma, json1.data[i]. emailPessoal, json1.data[i]. dataNascimento,json1.data[i].dataAdmissao, json1.data[i]. titulacao, json1.data[i]. sexo, json1.data[i]. usuarioId, json1.data[i]. senha, json1.data[i]. situacao, json1.data[i]. endereco, json1.data[i]. enderecoNumero, json1.data[i]. enderecoComplemento, json1.data[i]. cep);
                              //log.info List_1
		    List_2.addAll(json1.data[j]. codigo, json1.data[j]. nome, json1.data[j]. documento, json1.data[j]. matricula, json1.data[j]. email, json1.data[j]. codigoCOA, json1.data[j]. idPlataforma, json1.data[j]. emailPessoal, json1.data[j]. dataNascimento,json1.data[j].dataAdmissao, json1.data[j]. titulacao, json1.data[j]. sexo, json1.data[j]. usuarioId, json1.data[j]. senha, json1.data[j]. situacao, json1.data[j]. endereco, json1.data[j]. enderecoNumero, json1.data[j]. enderecoComplemento, json1.data[j]. cep); 
                             // log.info List_2
                              try
                              {
                                              assert List_1!=List_2;
                              }
                              catch(Exception e)
                              {
                                              log.info e
                              }
                 }
                }
}

