def response=messageExchange.response.responseContent;
def json1= new JsonSlurper().parseText(response);
def len=json1.data.size() ;
def List_1=[1];
def List_2=[1];

for(int i=1;i<len;i++)
{
                def value =json1.data[i].cursoCodigo;
                //log.info value
                for(int j=i+1;j<len;j++)
                {

                 def value_1 =json1.data[j].cursoCodigo;

                 if(value_1.equalsIgnoreCase(value))
                 {
                 	log.info "the i cod campus value: "+json1.data[i]. cursoCodigo;
                 	log.info json1.data[j]. cursoCodigo

              List_1.addAll(json1.data[i].cursoCodigo, json1.data[i]. habilitacaoCodigo, json1.data[i]. disciplinaCodigo, json1.data[i]. descricao, json1.data[i]. serie,json1.data[i]. modalidade, json1.data[i]. disciplinaTipo, json1.data[i]. matrizTipo, json1.data[i]. optativaTipo, json1.data[i]. cargaHorariaTotal,json1.data[i].cargaHorariaPratica, json1.data[i]. cargaHorariaTeorica, json1.data[i]. grupoCodigo, json1.data[i]. grupoCodigo);
                              //log.info List_1
		   List_2.addAll(json1.data[j].cursoCodigo, json1.data[j]. habilitacaoCodigo, json1.data[j]. disciplinaCodigo, json1.data[j]. descricao, json1.data[j]. serie	,json1.data[j]. modalidade, json1.data[j]. disciplinaTipo, json1.data[j]. matrizTipo, json1.data[j]. optativaTipo, json1.data[j]. cargaHorariaTotal,json1.data[j].cargaHorariaPratica, json1.data[j]. cargaHorariaTeorica, json1.data[j]. grupoCodigo, json1.data[j]. grupoCodigo);; 
                              //log.info List_2
                              try
                              {
                                              assert List_1!=List_2;
                              }
                              catch(Exception e)
                              {
                                              log.info e
                              }
                 }
                }
}

