import groovy.json.JsonSlurper
boolean length_flag=false;
def response=messageExchange.response.responseContent;
def json1= new JsonSlurper().parseText(response);
def len=json1.data.size() ;
//log.info len
int length;

// verifying the datatypes for all the records 
for(int i=0;i<len;i++)
{
                ///Description field validation

                def value =json1.data[i].termCode;
                try
                {               
                assert "true"==fieldlengthcheck("termCode_Length",value);
                }
                catch(Exception e)
                {
                	println (json1.data[i].description+": "+e)
                }

}

public int lengthcheck(String description)
{
                   length=description.length();
                   return length;
}

public String fieldlengthcheck(String Max_len_Field_Name,String ResponseFiledname)
{
              length_flag=false;                  //Getting the Max length from the test case

               String fieldlengthfromtestcase=context.getTestCase().getPropertyValue(Max_len_Field_Name);
                int length_testcase=Integer.parseInt(fieldlengthfromtestcase);

                if(lengthcheck(ResponseFiledname)<=length_testcase)


                {

                              length_flag=true;         
                           // log.info  lengthcheck(ResponseFiledname)
                }
                else
                {
                              length_flag=false;

                }
               log.info "before return : "+length_flag
                return length_flag.toString();
}

