	CodigoCursoLocal_resp=json1.data[j].CodigoCursoLocal;
	CodigoUnidadeEnsinoLocal_resp=json1.data[j].CodigoUnidadeEnsinoLocal;
	CodigoClassLocal_resp=json1.data[j].CodigoClassLocal;
	CodigoDetlLocal_resp=json1.data[j].CodigoDetlLocal;
	CodigoAttsLocal_resp=json1.data[j].CodigoAttsLocal;	
	CodigoDeptLocal_resp=json1.data[j].CodigoDeptLocal;
	PrecoMatricula_resp=json1.data[j].PrecoMatricula;


for( i=1;i<=act_val;i++)
{
//Getting Response from JDBC Request ***********Filed name should change for every api*****

CodigoIntakeLocal=xml.getNodeValue("//ResultSet//Row["+i+"]//CODIGOINTAKELOCAL");
log.info CodigoIntakeLocal;
def Tipo=xml.getNodeValue("//ResultSet//Row["+i+"]//TIPO");
def CodigoSequenceOferta=xml.getNodeValue("//ResultSet//Row["+i+"]//CODIGOSEQUENCEOFERTA")
