def CodigoCursoLocal=xml.getNodeValue("//ResultSet//Row["+i+"]//CODIGOCURSOLOCAL")
def CodigoUnidadeEnsinoLocal=xml.getNodeValue("//ResultSet//Row["+i+"]//CODIGOUNIDADEENSINOLOCAL")
def CodigoClassLocal=xml.getNodeValue("//ResultSet//Row["+i+"]//CODIGOCLASSLOCAL")
def CodigoDetlLocal=xml.getNodeValue("//ResultSet//Row["+i+"]//CODIGODETLLOCAL")
def CodigoAttsLocal=xml.getNodeValue("//ResultSet//Row["+i+"]//CODIGOATTSLOCAL")
def CodigoDeptLocal=xml.getNodeValue("//ResultSet//Row["+i+"]//CODIGODEPTLOCAL")
def PrecoMatriculaexo=xml.getNodeValue("//ResultSet//Row["+i+"]//PRECOMATRICULA")


if (CodigoIntakeLocal_resp.equals(CODIGOINTAKELOCAL))
{
	if(CodigoCursoLocal_resp.isEmpty()) 
	{
	}
