import groovy.json.JsonSlurper
boolean flar;
def response=messageExchange.response.responseContent;
def json1= new JsonSlurper().parseText(response);
def len=json1.data.size() ;
def List_1=[1];
def List_2=[1];

for(int i=1;i<len;i++)
{
                def value =json1.data[i].CodigoIntakeLocal;
                //log.info value
                for(int j=i+1;j<len;j++)
                {

                 def value_1 =json1.data[j].CodigoIntakeLocal;

                 if(value_1.equalsIgnoreCase(value))
                 {

              List_1.addAll(json1.data[i]. CodigoIntakeLocal, json1.data[i]. Tipo, json1.data[i]. CodigoSequenceOferta, json1.data[i]. CodigoTipoCursoLocal, json1.data[i]. CodigoUnidadeEnsinoLocal, json1.data[i]. CodigoClassLocal, json1.data[i]. CodigoDetLocal, json1.data[i]. CodigoAttsLocal, json1.data[i]. CodigoDeptLocal,json1.data[i].PrecoMatricula);
                             // log.info List_1
List_2.addAll(json1.data[j]. CodigoIntakeLocal, json1.data[j]. Tipo, json1.data[j]. CodigoSequenceOferta, json1.data[j]. CodigoTipoCursoLocal, json1.data[j]. CodigoUnidadeEnsinoLocal, json1.data[j]. CodigoClassLocal, json1.data[j]. CodigoDetLocal, json1.data[j]. CodigoAttsLocal, json1.data[j]. CodigoDeptLocal,json1.data[j].PrecoMatricula); 
                              //log.info List_2
                              try
                              {
                                              assert List_1!=List_2;
                              }
                              catch(Exception e)
                              {
                                              log.info e
                              }
                 }
                }
}

