import groovy.json.JsonSlurper
boolean flar;
def response=messageExchange.response.responseContent;
def json1= new JsonSlurper().parseText(response);
def len=json1.data.size() ;
def List_1=[1];
def List_2=[1];

for(int i=1;i<len;i++)
{
                def value =json1.data[i].codigo;
                //log.info value
                for(int j=i+1;j<len;j++)
                {

                 def value_1 =json1.data[j].codigo;

                 if(value.equalsIgnoreCase(value_1))
                 {

              List_1.addAll(json1.data[i].codigo,json1.data[i].descricao);

              List_2.addAll(json1.data[j].codigo,json1.data[j].descricao);

                              try
                              {
                                              assert List_1!=List_2;
                              }
                              catch(Exception e)
                              {
                                              log.info e
                              }
                 }
                }
}

con:soapui-project.con:afterRunScript:
def passed=0
def failed=0

for (testSuiteRunner in runner.results ) 
{
	for ( testCaseRunner in testSuiteRunner.results )
	{
    		for ( testStepResult in testCaseRunner.results )
    		{
    	 		if (testStepResult.status.toString()=="OK") passed++
    	 			else failed++
		}	
	}
}
log.info "passed="+passed+" failed="+failed
runner.project.setPropertyValue("passed", "$passed" ) 
runner.project.setPropertyValue("failed", "$failed" )
